            if emlak_tipi == "İ" or emlak_tipi == "i": #Emlak tipi işyeri oldugu durumda
                isyeri_satis_fiyat_toplami += satis_bedeli  #İsyeri satıs fiyat toplamını bulmak için döngü dondukce satıs bedeli eklenir
                satilan_isyeri += 1     #Satılan işyeri sayısını 1 arttırır
